<template>
  <body style="margin-top: 7rem; top: 0;">
    <div id="for_nav"></div>
    <div style="padding-top: 60px;" class="flex items-center justify-center">
      <h3 style="color: black;">현재 기분을 선택하면 알맞는 영화를 추천해드릴게요!</h3>
    </div>
    <div class="menu">
      <div class="toggle" @click="toggle"><img style="margin-left: 3px; height: 100px; width: 100px;" src="data:image/png;base64,"></div>
      <li style="--i:0;--clr:#fff;">
        <a id="sad" href="#" @click.prevent="goSad">
          <img style="width: 100px; height: 100px;" src="data:image/png;base64,">
        </a>
      </li>
      <li style="--i:1;--clr:#fff;">
        <a id="happy" href="#" @click.prevent="goHappy"><img style="width: 100px; height: 100px;" src="data:image/png;base64,"></a>
      </li>
      <li style="--i:2;--clr:#fff;">
        <a href="#" @click.prevent="goMusic"><img style="width: 100px; height: 100px;" src="data:image/png;base64,"></a>
      </li>
      <li style="--i:3;--clr:#fff;">
        <a href="#" @click.prevent="goAngry"><img style="width: 100px; height: 100px;" src="data:image/png;base64,"></a>
      </li>
      <li style="--i:4;--clr:#fff;">
        <a href="#" @click.prevent="goDontKnow"><img style="width: 100px; height: 100px;" src="data:image/png;base64,"></a>
      </li>
      <li style="--i:5;--clr:#fff;">
        <a href="#" @click.prevent="goLove"><img style="width: 100px; height: 100px;" src="data:image/png;base64,"></a>
      </li>
      
    </div>
  </body>
</template>

<script>
import router from '@/router';
import _ from 'lodash'
import { mapGetters } from 'vuex';

const moviesStore = 'moviesStore';

export default {
  name: 'FeelingMovieView',

  data() {
    return {
      
    };
  },
  computed:{
    ...mapGetters(moviesStore, ['genre36', 'genre10402', 'genre28', 'genre80', 'genre10749', 'genre10751']),
    forHappy() {
      return _.sample(this.genre36);
    },
    forMusic() {
      return _.sample(this.genre10402);
    },
    forAngry() {
      return _.sample(this.genre28);
    },
    forDontKnow() {
      return _.sample(this.genre80);
    },
    forLove() {
      return _.sample(this.genre10749);
    },
    forSad() {
      return _.sample(this.genre10751);
    },
  },
  mounted() {
  },
  methods: {
    toggle() {
      const menu = document.querySelector('.menu');
      menu.classList.toggle('active');
    },
    goHappy() {
      router.push({ name: 'MovieDetailInfoView', params: { id: this.forHappy.id } });
    },
    goMusic() {
      router.push({ name: 'MovieDetailInfoView', params: { id: this.forMusic.id } });
    },
    goAngry() {
      router.push({ name: 'MovieDetailInfoView', params: { id: this.forAngry.id } });
    },
    goDontKnow() {
      router.push({ name: 'MovieDetailInfoView', params: { id: this.forDontKnow.id } });
    },
    goLove() {
      router.push({ name: 'MovieDetailInfoView', params: { id: this.forLove.id } });
    },
    goSad() {
      router.push({ name: 'MovieDetailInfoView', params: { id: this.forSad.id } });
    },
  },
};
</script>

